addEvent ("onPlayerCoreLogin");addEventHandler ("onPlayerCoreLogin",root,	function()		if exports.database:getPlayerData (source:getData ("ID"), "userinfo", "linked.account") then			local acc = fromJSON (exports.database:getPlayerData (source:getData ("ID"), "userinfo", "linked.account"));			logIn(source, 				getAccount(					teaDecode (acc[1], source:getData ("ID")),					teaDecode (acc[2], source:getData ("ID"))				),				teaDecode (acc[2], source:getData ("ID"))			);			end	end)	addCommandHandler ("link", 	function (player, cmd, acc, pass)		local acc, pass = acc, pass;		if getPlayerAccount (player) then			pass = acc;			acc = getAccountName (getPlayerAccount (player));		end		if acc and pass then 			if getAccount (acc, pass) then 				if exports.database:setPlayerData (player:getData ("ID"), "userinfo", "linked.account", toJSON ({teaEncode (acc, player:getData ("ID")), teaEncode (pass, player:getData ("ID"))})) then 					exports.mtatr_hud:dm ("Hesaplar başarıyla birleştirildi!", player, 0, 255, 0);					return				end				end			end			exports.mtatr_hud:dm ("Bir hata oluştu. Lütfen hesap adı veya parolanızı girdiğinizden emin olun.", player, 255, 0, 0);	end);function isEventHandlerAdded( sEventName, pElementAttachedTo, func )	if 		type( sEventName ) == 'string' and 		isElement( pElementAttachedTo ) and 		type( func ) == 'function' 	then		local aAttachedFunctions = getEventHandlers( sEventName, pElementAttachedTo )		if type( aAttachedFunctions ) == 'table' and #aAttachedFunctions > 0 then			for i, v in ipairs( aAttachedFunctions ) do				if v == func then					return true				end			end		end	end 	return falseendfunction isPlayerInACL ( player, acl )	if not isElement (player) then return false end	local account = getPlayerAccount ( player )	if not account then return false end	if ( isGuestAccount ( account ) ) then		return false	end	return isObjectInACLGroup ( "user."..getAccountName ( account ), aclGetGroup ( acl ) )endfunction isPlayerAdmin (player)	return isPlayerInACL (player, "Admin")endlocal acl = {	"Admin",	"SuperModerator",	"Moderator",};function isPlayerInStaff (player)	for i, v in ipairs (acl) do 		if isPlayerInACL (player, v) then 			return true;		end		end		return false;endfunction getPlayerAdminLevel (player)	if isPlayerInStaff (player) then 		for i, v in ipairs (acl) do 			if isPlayerInACL (player, v) then 				return v;			end		end	end	return false;end	function _givePedJetPack (player)	if doesPedHaveJetPack(player) then 		removePedJetPack (player);	else		givePedJetPack (player);	endend	addCommandHandler ("jp", 	function (player)		if isPlayerInStaff (player) then 			_givePedJetPack (player);		end		end);	