addEventHandler ("onClientPlayerDamage", localPlayer, 	function (attacker)		if source == localPlayer and isElement ( attacker ) and attacker ~= source then 			if source:getData"indm" and (attacker:getData "indm" == source:getData "indm") then 				if source:getData ("indm"):find ("Zombi") then 					cancelEvent();				end				end		end		end);	addEvent ("onClientPlayerJoinsDeathmatch", true);addEventHandler ("onClientPlayerJoinsDeathmatch", root, 	function (type)		if type == "zombie" then 			setSkyGradient (200, 0, 0, 0, 0, 0);		end	end);	addEvent ("onClientPlayerLeavesDeathmatch", true);addEventHandler ("onClientPlayerLeavesDeathmatch", root, 	function ()		resetSkyGradient();	end);	