function dm (...)	local args = {...};	local elem = args[2];	table.remove (args, 2);	triggerClientEvent (elem, "hud.adddm", elem, unpack (args));end	function dNote (...)	local args = {...};	local element = args[3];	table.remove (args, 3);	triggerClientEvent (element, "lvdm.dNote", element, unpack (args));end	function dmn (t, element)	if t then 		local text, element = t or "", element or root		triggerClientEvent (element, "notifications.dmlabel", element, text)	end	end	